import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ConvertJson(api) {
  const apiParams = {};
  api.apiParams.forEach((param) => {
    apiParams[param.key] = param.value;
  });
  return apiParams;
}

function StatusDisplay() {
  const [apiInfo, setApiInfo] = useState([]);
  const [statusCodes, setStatusCodes] = useState({});
  const [lastApiRequestTime, setLastApiRequestTime] = useState({});

  useEffect(() => {
    const endpointData = require('./endpoints.json');
    const apiInfo = endpointData.apiInfo;

    setApiInfo(apiInfo);

    apiInfo.forEach((api) => {
      fetchStatusCode(api);
      setInterval(() => fetchStatusCode(api), api.requestIntervalMin * 60000);
    });

    return () => {
      apiInfo.forEach((api) => {
        clearInterval(api.intervalId);
      });
    }
  }, []);

  const fetchStatusCode = async (api) => {
    const params = ConvertJson(api);
    await axios.get(
      api.apiEndpoint,
      {params}
    ).then(response => {
      setStatusCodes((prevStatusCodes) => ({
        ...prevStatusCodes,
        [api.apiName]: response.status,
      }));
      setLastApiRequestTime((lastApiRequestTime) => ({
        ...lastApiRequestTime,
        [api.apiName]: new Date(),
      }));
    }).catch(error => {
      setStatusCodes((prevStatusCodes) => ({
        ...prevStatusCodes,
        [api.apiName]: error.response.status,
      }));
    })
  };

  return (
    <div>
      {apiInfo.map((api) => (
        <div key={api.apiName}>
          <p>API名: {api.apiName}</p>
          <p>ステータスコード: {statusCodes[api.apiName]}</p>
          <p>最終更新時間: {lastApiRequestTime ? lastApiRequestTime.toLocaleTimeString() : 'まだ送信されていません'}</p>
        </div>
      ))}
    </div>
  );
}

export default StatusDisplay;
