import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

export const getApi = createAsyncThunk('api/getApi', async () => {
  const apiData = await fetch('http://localhost:8080/api/fetch').then((res) =>
    res.json()
  );

  const apiSettingData = await Promise.all(
    apiData.map(async (apiItem) => {
      // api-setting
      const settingResponse = await fetch(`http://localhost:8080/api-setting/fetch?api_id=${apiItem.api_id}`);
      const settingDatas = await settingResponse.json();
      
      // api-header
      const headerResponse = await fetch(`http://localhost:8080/api-header-setting/fetch?api_setting_id=${settingData.api_setting_id}`);
      const headerDatas = await headerResponse.json();

      // api-param
      const paramResponse = await fetch(`http://localhost:8080/api-param-setting/fetch?api_setting_id=${settingData.api_setting_id}`);
      const paramDatas = await paramResponse.json();

      // api-history
      const historyResponse = await fetch(`http://localhost:8080/api-header-setting/fetch?api_setting_id=${settingData.api_setting_id}`);
      const historyDatas = await historyResponse.json();
    })
  );

  return apiSettingData;
});

export const apiSlice = createSlice({
  name: 'api',
  initialState: {
    api: [],
    loading: false,
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(getApi.pending, (state) => {
        state.loading = true;
      })
      .addCase(getApi.fulfilled, (state, action) => {
        state.loading = false;
        state.api = action.payload;
        console.log(state.api);
      })
      .addCase(getApi.rejected, (state) => {
        state.loading = false;
        state.error = true;
      });
  },
});

export default apiSlice.reducer;
