import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

// apiデータ取得
const fetchApiData = async () => {
  const response = await fetch('http://localhost:8080/api/fetch');
  return response.json();
};

// api-settingデータ取得
const fetchApiSettingData = async (apiId) => {
  const response = await fetch(`http://localhost:8080/api-setting/fetch?api_id=${apiId}`);
  return response.json();
};

// api-headerデータ取得
const fetchApiHeaderData = async (apiSettingId) => {
  const response = await fetch(`http://localhost:8080/api-header-setting/fetch?api_setting_id=${apiSettingId}`);
  return response.json();
};


export const getApi = createAsyncThunk('api/getApi', async () => {
  const apiData = await fetchApiData();

  const apiSettingDataPromises = apiData.map(async (apiItem) => {
    const apiSettingData = await fetchApiSettingData(apiItem.api_id);

    // 各api-settingデータに関連するapi-headerデータを取得する非同期処理を実行
    const apiSettingWithDataPromises = apiSettingData.map(async (settingItem) => {
      const headerData = await fetchApiHeaderData(settingItem.api_setting_id);
      return { ...settingItem, api_header: headerData };
    });

    // api-headerデータを取得したら、それをapi_settingフィールドに格納
    const apiSettingWithData = await Promise.all(apiSettingWithDataPromises);
    return { ...apiItem, api_setting: apiSettingWithData };
  });

  return Promise.all(apiSettingDataPromises);
});

export const apiSlice = createSlice({
  name: 'api',
  initialState: {
    api: [],
    loading: false,
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(getApi.pending, (state) => {
        state.loading = true;
      })
      .addCase(getApi.fulfilled, (state, action) => {
        state.loading = false;
        state.api = action.payload;
        console.log(state.api);
      })
      .addCase(getApi.rejected, (state) => {
        state.loading = false;
        state.error = true;
      });
  },
});

export default apiSlice.reducer;
