import React, { useState, useEffect } from 'react';

function StatusDisplay() {
  const [statusCode, setStatusCode] = useState(null);

  useEffect(() => {
    // エンドポイント情報を読み込む
    const endpointData = require('./endpoints.json');
    const { apiEndpoint, apiParameters } = endpointData;

    // APIを呼び出す関数
    const fetchStatusCode = async () => {
      try {
        // APIエンドポイントにGETリクエストを送信
        const response = await fetch(apiEndpoint, {
          method: 'GET',
          // パラメータをクエリストリングに追加する場合
          // body: new URLSearchParams(apiParameters),
        });

        // ステータスコードを取得
        const status = response.status;
        setStatusCode(status);
      } catch (error) {
        console.error('API呼び出し中にエラーが発生しました', error);
        // エラーの場合もステータスコードを表示
        setStatusCode('Error');
      }
    };

    // 初回の呼び出し
    fetchStatusCode();

    // 1時間ごとにAPIを呼び出す
    const intervalId = setInterval(fetchStatusCode, 3600000);

    // コンポーネントがアンマウントされたときにクリア
    return () => clearInterval(intervalId);
  }, []);

  // ステータスコードに応じて表示するメッセージを設定
  let statusMessage;
  if (statusCode === 200) {
    statusMessage = '正常なステータスコードです。';
  } else {
    statusMessage = `ステータスコード: ${statusCode}`;
  }

  return (
    <div>
      <p>{statusMessage}</p>
    </div>
  );
}

export default StatusDisplay;
