import { createSlice } from '@reduxjs/toolkit';

export const getApi = createAsyncThunk('api/getApi', async () => {
  return await fetch('http://localhost:8080/api/fetch').then((res) =>
    res.json()
  );
});

export const apiSlice = createSlice({
  name: 'api',
  initialState: {
    data: [],
    loading: false,
    error: null,
  },
  extraReducers: {
    [getApi.pending]: (state) => {
      state.loading = true;
    },
    [getApi.fulfilled]: (state, action) => {
      state.loading = false;
      state.api = action.payload;
    },
    [getApi.reiected]: (state) => {
      state.loading = false;
      state.error = true;
    }
  },
});

export default apiSlice.reducer;
