import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

export const getApi = createAsyncThunk('api/getApi', async () => {
  console.log("実行されてる？")
  return await fetch('http://localhost:8080/api/fetch').then((res) =>
    res.json()
  );
});

export const apiSlice = createSlice({
  name: 'api',
  initialState: {
    api: [],
    loading: false,
    error: null,
  },
  reducers: {
    getApiPending: (state) => {
      state.loading = true;
    },
    getApiFulfilled: (state, action) => {
      state.loading = false;
      state.api = action.payload;
    },
    getApiRejected: (state) => {
      state.loading = false;
      state.error = true;
    },
  },
});

export const { getApiPending, getApiFulfilled, getApiRejected } = apiSlice.actions;

export default apiSlice.reducer;
